        this.color = '#8B4513';
        this.name = 'Shotgun';
        break;
      case ItemType.SNIPER_RIFLE:
        this.color = '#2F4F4F';
        this.name = 'Sniper Rifle';
        break;
      case ItemType.MACHINE_GUN:
        this.color = '#556B2F';
        this.name = 'Machine Gun';
        break;
      case ItemType.LASER_GUN:
