        this.name = 'Laser Gun';
        break;
      default:
        this.color = '#ffffff';
        this.name = 'Unknown';
    }
  }
}

client/src/lib/game/Obstacle.ts.content:
export enum ObstacleType {
  WALL = 'wall',
  BARRICADE = 'barricade',
  EXPLOSIVE_BARREL = 'explosive_barrel',
  COVER = 'cover'
}

export class Obstacle {
  public x: number;
  public y: number;
  public width!: number;
  public height!: number;
  public health!: number;
  public maxHealth!: number;
  public type: ObstacleType;
  public color!: string;
  public destructible!: boolean;
  public explosive!: boolean;
  public explosionRadius!: number;
  public explosionDamage!: number;

  constructor(x: number, y: number, type: ObstacleType) {
    this.x = x;
    this.y = y;
    this.type = type;
    this.setupObstacle();
  }

  private setupObstacle() {
    switch (this.type) {
      case ObstacleType.WALL:
        this.width = 60;
        this.height = 15;
        this.health = -1; // Indestructible
        this.maxHealth = -1;
        this.color = '#808080';
        this.destructible = false;
        this.explosive = false;
        this.explosionRadius = 0;
        this.explosionDamage = 0;
        break;

      case ObstacleType.BARRICADE:
        this.width = 40;
        this.height = 12;
        this.health = 50;
        this.maxHealth = 50;
        this.color = '#8B4513';
        this.destructible = true;
        this.explosive = false;
        this.explosionRadius = 0;
        this.explosionDamage = 0;
        break;

      case ObstacleType.EXPLOSIVE_BARREL:
        this.width = 20;
        this.height = 25;
        this.health = 20;
        this.maxHealth = 20;
        this.color = '#FF6B35';
        this.destructible = true;
        this.explosive = true;
        this.explosionRadius = 80;
        this.explosionDamage = 40;
        break;

      case ObstacleType.COVER:
        this.width = 30;
        this.height = 10;
        this.health = 30;
        this.maxHealth = 30;
        this.color = '#696969';
        this.destructible = true;
        this.explosive = false;
        this.explosionRadius = 0;
        this.explosionDamage = 0;
        break;

      default:
        this.width = 20;
        this.height = 20;
        this.health = 10;
        this.maxHealth = 10;
        this.color = '#666666';
        this.destructible = true;
        this.explosive = false;
        this.explosionRadius = 0;
        this.explosionDamage = 0;
    }
  }

  public takeDamage(damage: number): boolean {
    if (!this.destructible || this.health === -1) return false;

    this.health = Math.max(0, this.health - damage);
    return true;
  }

  public isDestroyed(): boolean {
    return this.destructible && this.health <= 0;
  }

  public getHealthPercentage(): number {
    if (this.health === -1 || this.maxHealth === -1) return 1;
    return this.health / this.maxHealth;
  }

  public shouldExplode(): boolean {
    return this.explosive && this.isDestroyed();
  }

  // Check if point is inside obstacle for collision
  public containsPoint(x: number, y: number): boolean {
    return x >= this.x - this.width / 2 &&
           x <= this.x + this.width / 2 &&
           y >= this.y - this.height / 2 &&
           y <= this.y + this.height / 2;
  }

  // Generate random obstacles for the map
  public static generateRandomObstacles(
    canvasWidth: number, 
    canvasHeight: number, 
    playerX: number, 
    playerY: number
  ): Obstacle[] {
    const obstacles: Obstacle[] = [];
    const obstacleCount = 8 + Math.floor(Math.random() * 5); // 8-12 obstacles

    for (let i = 0; i < obstacleCount; i++) {
      let x: number, y: number;
      let attempts = 0;

      // Try to place obstacle away from player and other obstacles
      do {
        x = Math.random() * (canvasWidth - 100) + 50;
        y = Math.random() * (canvasHeight - 100) + 50;
        attempts++;
      } while (attempts < 20 && (
        Math.sqrt(Math.pow(x - playerX, 2) + Math.pow(y - playerY, 2)) < 100 ||
        obstacles.some(obs => 
          Math.sqrt(Math.pow(x - obs.x, 2) + Math.pow(y - obs.y, 2)) < 60
        )
      ));

      // Choose random obstacle type with different probabilities
      const rand = Math.random();
      let type: ObstacleType;

      if (rand < 0.3) {
        type = ObstacleType.WALL;
      } else if (rand < 0.6) {
        type = ObstacleType.BARRICADE;
      } else if (rand < 0.8) {
        type = ObstacleType.COVER;
      } else {
        type = ObstacleType.EXPLOSIVE_BARREL;
      }

      obstacles.push(new Obstacle(x, y, type));
    }

    return obstacles;
  }
}

client/src/lib/game/Player.ts.content:
import { Bullet } from './Bullet';
import { Item } from './Item';
import { InputManager } from './InputManager';
import { Weapon, WeaponType } from './Weapon';

export class Player {
  public x: number;
  public y: number;
  public width = 20;
  public height = 20;
  public health = 100;
  public maxHealth = 100;
  public speed = 150;
  public baseSpeed = 150;
  public weaponDamage = 10;
  public baseWeaponDamage = 10;
  public inventory: Item | null = null;
  public weapon: Weapon;
  public weapons: Weapon[] = [];
  public currentWeaponIndex = 0;
  public lastAimX = 0;
  public lastAimY = -1; // Default aim up

  private lastShot = 0;
  private shootCooldown = 200; // milliseconds
  private lastDamageTaken = 0;
  private damageCooldown = 1000; // 1 second invincibility
  private lastPoisonTick = 0;
  private poisonCooldown = 300; // 300ms between poison damage ticks
  private speedBoostEndTime = 0;
  private weaponUpgradeEndTime = 0;

  constructor(x: number, y: number) {
    this.x = x;
    this.y = y;

    // Initialize with pistol
    this.weapon = new Weapon(WeaponType.PISTOL);
    this.weapons.push(this.weapon);
  }

  public handleInput(inputManager: InputManager, deltaTime: number) {
    const moveSpeed = (this.speed * deltaTime) / 1000;

    if (inputManager.isKeyPressed('KeyW') || inputManager.isKeyPressed('ArrowUp')) {
      this.y -= moveSpeed;
    }
    if (inputManager.isKeyPressed('KeyS') || inputManager.isKeyPressed('ArrowDown')) {
      this.y += moveSpeed;
    }
    if (inputManager.isKeyPressed('KeyA') || inputManager.isKeyPressed('ArrowLeft')) {
      this.x -= moveSpeed;
    }
    if (inputManager.isKeyPressed('KeyD') || inputManager.isKeyPressed('ArrowRight')) {
      this.x += moveSpeed;
    }
  }

  public update(deltaTime: number, canvasWidth: number, canvasHeight: number) {
    // Keep player within bounds
    this.x = Math.max(this.width / 2, Math.min(canvasWidth - this.width / 2, this.x));
    this.y = Math.max(this.height / 2, Math.min(canvasHeight - this.height / 2, this.y));

    // Check for effect expiration
    const now = Date.now();

    if (this.speedBoostEndTime > 0 && now > this.speedBoostEndTime) {
      this.speed = this.baseSpeed;
      this.speedBoostEndTime = 0;
    }

    if (this.weaponUpgradeEndTime > 0 && now > this.weaponUpgradeEndTime) {
      this.weaponDamage = this.baseWeaponDamage;
      this.weaponUpgradeEndTime = 0;
    }
  }

  public shoot(targetX: number, targetY: number): Bullet[] {
    const now = Date.now();
    if (now - this.lastShot < this.weapon.stats.fireRate) return [];
    if (!this.weapon.canFire()) return [];

    this.lastShot = now;
    this.weapon.fire();

    const dx = targetX - this.x;
    const dy = targetY - this.y;
    const length = Math.sqrt(dx * dx + dy * dy);

    if (length === 0) return [];

    const baseDirX = dx / length;
    const baseDirY = dy / length;

    const bullets: Bullet[] = [];

    // Create bullets based on weapon type
    for (let i = 0; i < this.weapon.stats.bulletCount; i++) {
      let dirX = baseDirX;
      let dirY = baseDirY;

      // Apply spread for shotgun and machine gun
      if (this.weapon.stats.spread > 0) {
        const spreadAngle = (Math.random() - 0.5) * this.weapon.stats.spread;
        const cos = Math.cos(spreadAngle);
        const sin = Math.sin(spreadAngle);

        const newDirX = dirX * cos - dirY * sin;
        const newDirY = dirX * sin + dirY * cos;

        dirX = newDirX;
        dirY = newDirY;
      }

      // Apply weapon damage multiplier for upgrades
      const effectiveDamage = this.weapon.stats.damage * (this.weaponDamage / this.baseWeaponDamage);

      // Store aim direction for continuous shooting
      this.lastAimX = baseDirX;
      this.lastAimY = baseDirY;

      bullets.push(new Bullet(
        this.x, 
        this.y, 
        dirX, 
        dirY,
        effectiveDamage,
        this.weapon.stats.bulletSpeed,
        this.weapon.stats.piercing,
        this.weapon.stats.color,
        this.weapon.stats.range
      ));
    }

    return bullets;
  }

  public shootContinuous(deltaTime: number): Bullet[] {
    const now = Date.now();
    if (now - this.lastShot < this.weapon.stats.fireRate) return [];
    if (!this.weapon.canFire()) return [];

    this.lastShot = now;
    this.weapon.fire();

    const bullets: Bullet[] = [];

    // Create bullets based on weapon type
    for (let i = 0; i < this.weapon.stats.bulletCount; i++) {
      let dirX = this.lastAimX;
      let dirY = this.lastAimY;

      // Apply spread for shotgun and machine gun
      if (this.weapon.stats.spread > 0) {
        const spreadAngle = (Math.random() - 0.5) * this.weapon.stats.spread;
        const cos = Math.cos(spreadAngle);
        const sin = Math.sin(spreadAngle);

        const newDirX = dirX * cos - dirY * sin;
        const newDirY = dirX * sin + dirY * cos;

        dirX = newDirX;
        dirY = newDirY;
      }

      // Apply weapon damage multiplier for upgrades
      const effectiveDamage = this.weapon.stats.damage * (this.weaponDamage / this.baseWeaponDamage);

      bullets.push(new Bullet(
        this.x, 
        this.y, 
        dirX, 
        dirY,
        effectiveDamage,
        this.weapon.stats.bulletSpeed,
        this.weapon.stats.piercing,
        this.weapon.stats.color,
        this.weapon.stats.range
      ));
    }

    return bullets;
  }

  public takeDamage(damage: number): boolean {
    const now = Date.now();
    if (now - this.lastDamageTaken < this.damageCooldown) return false;

    this.lastDamageTaken = now;
    this.health = Math.max(0, this.health - damage);
    return true;
  }

  public heal(amount: number) {
    this.health = Math.min(this.maxHealth, this.health + amount);
  }

  public applySpeedBoost(duration: number) {
    this.speed = this.baseSpeed * 1.5;
    this.speedBoostEndTime = Date.now() + duration;
  }

  public applyWeaponUpgrade(duration: number) {
    this.weaponDamage = 20;
    this.weaponUpgradeEndTime = Date.now() + duration;
  }

  public isInvulnerable(): boolean {
    return Date.now() - this.lastDamageTaken < this.damageCooldown;
  }

  public takePoisonDamage(damage: number): boolean {
    const now = Date.now();
    if (now - this.lastPoisonTick < this.poisonCooldown) return false;

    this.lastPoisonTick = now;
    this.health = Math.max(0, this.health - damage);
    return true;
  }

  public addWeapon(weaponType: WeaponType) {
    // Check if we already have this weapon
    const existingWeapon = this.weapons.find(w => w.type === weaponType);
    if (existingWeapon) {
      existingWeapon.reload(); // Just reload if we already have it
      return;
    }

    const newWeapon = new Weapon(weaponType);
    this.weapons.push(newWeapon);
    this.switchToWeapon(this.weapons.length - 1);
  }

  public switchToWeapon(index: number) {
    if (index >= 0 && index < this.weapons.length) {
      this.currentWeaponIndex = index;
      this.weapon = this.weapons[index];
    }
  }

  public switchWeapon() {
    this.currentWeaponIndex = (this.currentWeaponIndex + 1) % this.weapons.length;
    this.weapon = this.weapons[this.currentWeaponIndex];
  }

  public reloadWeapon(): boolean {
    if (this.weapon.ammo === this.weapon.maxAmmo || this.weapon.maxAmmo === -1) {
      return false; // Already full or infinite ammo
    }
    this.weapon.reload();
    return true; // Reloaded successfully
  }
}

client/src/lib/game/Renderer.ts.content:
import { Player } from './Player';
import { Zombie, ZombieType } from './Zombie';
import { Bullet } from './Bullet';
import { Item } from './Item';
import { GameState } from './GameState';
import { Obstacle } from './Obstacle';

export class Renderer {
  private ctx: CanvasRenderingContext2D;

  constructor(ctx: CanvasRenderingContext2D) {
    this.ctx = ctx;
  }

  public renderPlayer(player: Player) {
    this.ctx.save();

    // Flash red if invulnerable
    if (player.isInvulnerable()) {
      this.ctx.globalAlpha = 0.5;
    }

    this.ctx.fillStyle = '#4CAF50';
    this.ctx.fillRect(
      player.x - player.width / 2,
      player.y - player.height / 2,
      player.width,
      player.height
    );

    // Draw health bar above player
    const barWidth = 30;
    const barHeight = 4;
    const barX = player.x - barWidth / 2;
    const barY = player.y - player.height / 2 - 10;

    this.ctx.fillStyle = '#ff0000';
    this.ctx.fillRect(barX, barY, barWidth, barHeight);

    this.ctx.fillStyle = '#00ff00';
    const healthWidth = (player.health / player.maxHealth) * barWidth;
    this.ctx.fillRect(barX, barY, healthWidth, barHeight);

    this.ctx.restore();
  }

  public renderZombie(zombie: Zombie) {
    this.ctx.save();

    // Special effects for berserker when enraged
    if (zombie.type === ZombieType.BERSERKER && zombie.enraged) {
      this.ctx.shadowColor = '#ff0000';
      this.ctx.shadowBlur = 10;
    }

    this.ctx.fillStyle = zombie.color;
    this.ctx.fillRect(
      zombie.x - zombie.width / 2,
      zombie.y - zombie.height / 2,
      zombie.width,
      zombie.height
    );

    // Draw shield for shield zombies
    if (zombie.type === ZombieType.SHIELD && zombie.shieldHealth > 0) {
      this.ctx.strokeStyle = '#00aaff';
      this.ctx.lineWidth = 3;
      this.ctx.globalAlpha = 0.7;
      this.ctx.strokeRect(
        zombie.x - zombie.width / 2 - 3,
        zombie.y - zombie.height / 2 - 3,
        zombie.width + 6,
        zombie.height + 6
      );
      this.ctx.globalAlpha = 1.0;
    }

    // Draw crown for boss zombies
    if (zombie.type === ZombieType.BOSS) {
      this.ctx.fillStyle = '#ffaa00';
      this.ctx.fillRect(
        zombie.x - 8,
        zombie.y - zombie.height / 2 - 8,
        16,
        6
      );
      // Crown points
      this.ctx.fillRect(zombie.x - 6, zombie.y - zombie.height / 2 - 10, 3, 4);
      this.ctx.fillRect(zombie.x - 1, zombie.y - zombie.height / 2 - 12, 3, 6);
      this.ctx.fillRect(zombie.x + 4, zombie.y - zombie.height / 2 - 10, 3, 4);
    }

    // Draw health bar for boss and heavy zombies
    if ((zombie.type === ZombieType.HEAVY || zombie.type === ZombieType.BOSS) && zombie.health < zombie.maxHealth) {
      const barWidth = zombie.type === ZombieType.BOSS ? 50 : 30;
      const barHeight = 4;
      const barX = zombie.x - barWidth / 2;
      const barY = zombie.y - zombie.height / 2 - (zombie.type === ZombieType.BOSS ? 15 : 8);

      this.ctx.fillStyle = '#ff0000';
      this.ctx.fillRect(barX, barY, barWidth, barHeight);

      this.ctx.fillStyle = zombie.type === ZombieType.BOSS ? '#ffaa00' : '#ffaa00';
      const healthWidth = zombie.getHealthPercentage() * barWidth;
      this.ctx.fillRect(barX, barY, healthWidth, barHeight);
    }

    // Draw shield bar for shield zombies
    if (zombie.type === ZombieType.SHIELD && zombie.shieldHealth > 0) {
      const barWidth = 25;
      const barHeight = 3;
      const barX = zombie.x - barWidth / 2;
      const barY = zombie.y + zombie.height / 2 + 3;

      this.ctx.fillStyle = '#0066cc';
      this.ctx.fillRect(barX, barY, barWidth, barHeight);

      this.ctx.fillStyle = '#00aaff';
      const shieldWidth = zombie.getShieldPercentage() * barWidth;
      this.ctx.fillRect(barX, barY, shieldWidth, barHeight);
    }

    this.ctx.restore();
  }

  public renderBullet(bullet: Bullet) {
    this.ctx.fillStyle = bullet.color;

    // Make piercing bullets slightly larger and add glow effect
    if (bullet.piercing) {
      this.ctx.save();
      this.ctx.shadowColor = bullet.color;
      this.ctx.shadowBlur = 5;
      this.ctx.fillRect(
        bullet.x - bullet.width / 2 - 1,
        bullet.y - bullet.height / 2 - 1,
        bullet.width + 2,
        bullet.height + 2
      );
      this.ctx.restore();
    } else {
      this.ctx.fillRect(
        bullet.x - bullet.width / 2,
        bullet.y - bullet.height / 2,
        bullet.width,
        bullet.height
      );
    }
  }

  public renderItem(item: Item) {
    this.ctx.fillStyle = item.color;
    this.ctx.fillRect(
      item.x - item.width / 2,
      item.y - item.height / 2,
      item.width,
      item.height
    );

    // Draw border
    this.ctx.strokeStyle = '#ffffff';
    this.ctx.lineWidth = 2;
    this.ctx.strokeRect(
      item.x - item.width / 2,
      item.y - item.height / 2,
      item.width,
      item.height
    );
  }

  public renderUI(gameState: GameState, player: Player) {
    this.ctx.save();
    this.ctx.font = '16px Inter, Arial, sans-serif';

    // Health (bottom left)
    this.ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
    this.ctx.fillRect(10, this.ctx.canvas.height - 50, 120, 35);

    this.ctx.fillStyle = '#ffffff';
    this.ctx.fillText(`HP: ${player.health}/${player.maxHealth}`, 20, this.ctx.canvas.height - 25);

    // Score and time (top center)
    const scoreText = `Score: ${gameState.score}`;
    const timeText = `Time: ${gameState.getSurvivalTimeFormatted()}`;
    const scoreWidth = this.ctx.measureText(scoreText).width;
    const timeWidth = this.ctx.measureText(timeText).width;
    const maxWidth = Math.max(scoreWidth, timeWidth);

    this.ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
    this.ctx.fillRect(this.ctx.canvas.width / 2 - maxWidth / 2 - 10, 10, maxWidth + 20, 50);

    this.ctx.fillStyle = '#ffffff';
    this.ctx.fillText(scoreText, this.ctx.canvas.width / 2 - scoreWidth / 2, 30);
    this.ctx.fillText(timeText, this.ctx.canvas.width / 2 - timeWidth / 2, 50);

    // Weapon info (bottom right)
    this.ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
    this.ctx.fillRect(this.ctx.canvas.width - 180, this.ctx.canvas.height - 90, 170, 80);

    this.ctx.fillStyle = '#ffffff';
    this.ctx.font = '14px Inter, Arial, sans-serif';
    this.ctx.fillText(`Weapon: ${player.weapon.name}`, this.ctx.canvas.width - 175, this.ctx.canvas.height - 70);
    this.ctx.fillText(`Ammo: ${player.weapon.getAmmoText()}`, this.ctx.canvas.width - 175, this.ctx.canvas.height - 50);

    this.ctx.font = '12px Inter, Arial, sans-serif';
    this.ctx.fillText('Q: Switch | R: Reload | 1-5: Select', this.ctx.canvas.width - 175, this.ctx.canvas.height - 30);

    // Inventory (top right, smaller)
    this.ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
    this.ctx.fillRect(this.ctx.canvas.width - 130, this.ctx.canvas.height - 160, 120, 60);

    this.ctx.fillStyle = '#ffffff';
    this.ctx.font = '14px Inter, Arial, sans-serif';
    this.ctx.fillText('Item (E):', this.ctx.canvas.width - 125, this.ctx.canvas.height - 140);

    if (player.inventory) {
      this.ctx.fillStyle = player.inventory.color;
      this.ctx.fillRect(this.ctx.canvas.width - 100, this.ctx.canvas.height - 125, 16, 16);

      this.ctx.fillStyle = '#ffffff';
      this.ctx.font = '12px Inter, Arial, sans-serif';
      this.ctx.fillText(player.inventory.name, this.ctx.canvas.width - 80, this.ctx.canvas.height - 113);
    } else {
      this.ctx.fillStyle = '#666666';
      this.ctx.font = '12px Inter, Arial, sans-serif';
      this.ctx.fillText('Empty', this.ctx.canvas.width - 100, this.ctx.canvas.height - 120);
    }

    this.ctx.restore();
  }

  public renderPoisonTrails(zombies: any[]) {
    this.ctx.save();

    zombies.forEach(zombie => {
      if (zombie.type === ZombieType.POISON) {
        const trail = zombie.getPoisonTrail();
        const now = Date.now();

        trail.forEach((point: {x: number, y: number, time: number}) => {
          const age = now - point.time;
          const alpha = Math.max(0, 1 - age / 5000); // Fade over 5 seconds

          this.ctx.globalAlpha = alpha * 0.6;
          this.ctx.fillStyle = '#00ff00';
          this.ctx.fillRect(point.x - 8, point.y - 8, 16, 16);
        });
      }
    });

    this.ctx.restore();
  }

  public renderGameOver(gameState: GameState, canvasWidth: number, canvasHeight: number) {
    this.ctx.save();

    // Background overlay
    this.ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
    this.ctx.fillRect(0, 0, canvasWidth, canvasHeight);

    // Game Over text
    this.ctx.fillStyle = '#ffffff';
    this.ctx.font = 'bold 48px Inter, Arial, sans-serif';
    this.ctx.textAlign = 'center';
    this.ctx.fillText('GAME OVER', canvasWidth / 2, canvasHeight / 2 - 100);

    // Stats
    this.ctx.font = '24px Inter, Arial, sans-serif';
    this.ctx.fillText(`Final Score: ${gameState.score}`, canvasWidth / 2, canvasHeight / 2 - 40);
    this.ctx.fillText(`Survival Time: ${gameState.getSurvivalTimeFormatted()}`, canvasWidth / 2, canvasHeight / 2);

    // Restart instruction
    this.ctx.font = '18px Inter, Arial, sans-serif';
    this.ctx.fillText('Click to restart', canvasWidth / 2, canvasHeight / 2 + 60);

    this.ctx.restore();

    // Add click listener for restart
    const restartHandler = () => {
      window.location.reload();
    };

    this.ctx.canvas.addEventListener('click', restartHandler, { once: true });
  }

  public renderObstacle(obstacle: Obstacle) {
    this.ctx.save();

    // Add glow effect for explosive barrels
    if (obstacle.type === 'explosive_barrel') {
      this.ctx.shadowColor = '#FF6B35';
      this.ctx.shadowBlur = 8;
    }

    this.ctx.fillStyle = obstacle.color;
    this.ctx.fillRect(
      obstacle.x - obstacle.width / 2,
      obstacle.y - obstacle.height / 2,
      obstacle.width,
      obstacle.height
    );

    // Draw health bar for destructible obstacles if damaged
    if (obstacle.destructible && obstacle.health < obstacle.maxHealth && obstacle.health > 0) {
      const barWidth = Math.max(20, obstacle.width * 0.8);
      const barHeight = 3;
      const barX = obstacle.x - barWidth / 2;
      const barY = obstacle.y - obstacle.height / 2 - 8;

      this.ctx.fillStyle = '#ff0000';
      this.ctx.fillRect(barX, barY, barWidth, barHeight);

      this.ctx.fillStyle = '#00ff00';
      const healthWidth = obstacle.getHealthPercentage() * barWidth;
      this.ctx.fillRect(barX, barY, healthWidth, barHeight);
    }

    // Add special markers
    if (obstacle.type === 'explosive_barrel') {
      // Warning symbol
      this.ctx.fillStyle = '#FFFF00';
      this.ctx.fillRect(obstacle.x - 3, obstacle.y - 8, 6, 6);
      this.ctx.fillStyle = '#FF0000';
      this.ctx.fillRect(obstacle.x - 1, obstacle.y - 6, 2, 2);
    } else if (obstacle.type === 'wall') {
      // Add brick pattern
      this.ctx.strokeStyle = '#606060';
      this.ctx.lineWidth = 1;
      for (let i = 0; i < 3; i++) {
        const yPos = obstacle.y - obstacle.height / 2 + (i * obstacle.height / 3);
        this.ctx.beginPath();
        this.ctx.moveTo(obstacle.x - obstacle.width / 2, yPos);
        this.ctx.lineTo(obstacle.x + obstacle.width / 2, yPos);
        this.ctx.stroke();
      }
    }

    this.ctx.restore();
  }
}

client/src/lib/game/Weapon.ts.content:
export enum WeaponType {
  PISTOL = 'pistol',
  SHOTGUN = 'shotgun',
  SNIPER = 'sniper',
  MACHINE_GUN = 'machine_gun',
  LASER = 'laser'
}

export interface WeaponStats {
  damage: number;
  fireRate: number; // milliseconds between shots
  bulletCount: number; // bullets per shot (for shotgun)
  spread: number; // spread angle in radians (for shotgun)
  bulletSpeed: number;
  piercing: boolean; // can bullets pass through zombies?
  range: number; // max distance bullets travel
  color: string; // bullet color
}

export class Weapon {
  public type: WeaponType;
  public name!: string;
  public stats!: WeaponStats;
  public ammo: number = -1; // -1 means infinite
  public maxAmmo: number = -1;

  constructor(type: WeaponType) {
    this.type = type;
    this.setupWeapon();
  }

  private setupWeapon() {
    switch (this.type) {
      case WeaponType.PISTOL:
        this.name = 'Pistol';
        this.stats = {
          damage: 10,
          fireRate: 200,
          bulletCount: 1,
          spread: 0,
          bulletSpeed: 400,
          piercing: false,
          range: 600,
          color: '#ffff00'
        };
        break;

      case WeaponType.SHOTGUN:
        this.name = 'Shotgun';
        this.stats = {
          damage: 8,
          fireRate: 800,
          bulletCount: 5,
          spread: Math.PI / 6, // 30 degrees spread
          bulletSpeed: 300,
          piercing: false,
          range: 300,
          color: '#ff8800'
        };
        this.ammo = 12;
        this.maxAmmo = 12;
        break;

      case WeaponType.SNIPER:
        this.name = 'Sniper Rifle';
        this.stats = {
          damage: 50,
          fireRate: 1500,
          bulletCount: 1,
          spread: 0,
          bulletSpeed: 800,
          piercing: true,
          range: 1000,
          color: '#00ff88'
        };
        this.ammo = 5;
        this.maxAmmo = 5;
        break;

      case WeaponType.MACHINE_GUN:
        this.name = 'Machine Gun';
        this.stats = {
          damage: 6,
          fireRate: 100,
          bulletCount: 1,
          spread: Math.PI / 24, // slight spread
          bulletSpeed: 450,
          piercing: false,
          range: 500,
          color: '#ff4444'
        };
        this.ammo = 50;
        this.maxAmmo = 50;
        break;

      case WeaponType.LASER:
        this.name = 'Laser Gun';
        this.stats = {
          damage: 15,
          fireRate: 300,
          bulletCount: 1,
          spread: 0,
          bulletSpeed: 600,
          piercing: true,
          range: 800,
          color: '#00ffff'
        };
        this.ammo = 20;
        this.maxAmmo = 20;
        break;

      default:
        this.name = 'Unknown';
        this.stats = {
          damage: 10,
          fireRate: 200,
          bulletCount: 1,
          spread: 0,
          bulletSpeed: 400,
          piercing: false,
          range: 600,
          color: '#ffff00'
        };
    }
  }

  public canFire(): boolean {
    return this.ammo !== 0;
  }

  public fire(): boolean {
    if (!this.canFire()) return false;

    if (this.ammo > 0) {
      this.ammo--;
    }
    return true;
  }

  public reload() {
    this.ammo = this.maxAmmo;
  }

  public getAmmoText(): string {
    if (this.ammo === -1) return 'âˆž';
    return `${this.ammo}/${this.maxAmmo}`;
  }
}

client/src/lib/game/Zombie.ts.content:
export enum ZombieType {
  NORMAL = 'normal',
  FAST = 'fast',
  SPLITTER = 'splitter',
  HEAVY = 'heavy',
  SPLITTER_CHILD = 'splitter_child',
  BOSS = 'boss',
  POISON = 'poison',
  SHIELD = 'shield',
  BERSERKER = 'berserker'
}

export class Zombie {
  public x: number;
  public y: number;
  public width: number;
  public height: number;
  public health: number;
  public maxHealth: number;
  public speed: number;
  public damage: number;
  public scoreValue: number;
  public type: ZombieType;
  public color: string;
  public shieldHealth: number = 0;
  public poisonTrail: Array<{x: number, y: number, time: number}> = [];
  public lastMinionSpawn: number = 0;
  public enraged: boolean = false;
  public baseSpeed: number;

  constructor(x: number, y: number, type: ZombieType) {
    this.x = x;
    this.y = y;
    this.type = type;

    switch (type) {
      case ZombieType.NORMAL:
        this.width = 18;
        this.height = 18;
        this.health = 10;
        this.maxHealth = 10;
        this.speed = 30;
        this.damage = 10;
        this.scoreValue = 10;
        this.color = '#4a5a3a';
        break;

      case ZombieType.FAST:
        this.width = 16;
        this.height = 16;
        this.health = 20;
        this.maxHealth = 20;
        this.speed = 80;
        this.damage = 10;
        this.scoreValue = 20;
        this.color = '#7a4a3a';
        break;

      case ZombieType.SPLITTER:
        this.width = 20;
        this.height = 20;
        this.health = 10;
        this.maxHealth = 10;
        this.speed = 40;
        this.damage = 5;
        this.scoreValue = 15;
        this.color = '#5a3a7a';
        break;

      case ZombieType.HEAVY:
        this.width = 24;
        this.height = 24;
        this.health = 40;
        this.maxHealth = 40;
        this.speed = 20;
        this.damage = 20;
        this.scoreValue = 30;
        this.color = '#6a2a2a';
        break;

      case ZombieType.SPLITTER_CHILD:
        this.width = 12;
        this.height = 12;
        this.health = 5;
        this.maxHealth = 5;
        this.speed = 60;
        this.damage = 5;
        this.scoreValue = 5;
        this.color = '#3a2a4a';
        break;

      case ZombieType.BOSS:
        this.width = 40;
        this.height = 40;
        this.health = 100;
        this.maxHealth = 100;
        this.speed = 15;
        this.damage = 30;
        this.scoreValue = 100;
        this.color = '#8a1a1a';
        break;

      case ZombieType.POISON:
        this.width = 18;
        this.height = 18;
        this.health = 15;
        this.maxHealth = 15;
        this.speed = 25;
        this.damage = 8;
        this.scoreValue = 25;
        this.color = '#2a5a2a';
        break;

      case ZombieType.SHIELD:
        this.width = 20;
        this.height = 20;
        this.health = 15;
        this.maxHealth = 15;
        this.speed = 35;
        this.damage = 12;
        this.scoreValue = 35;
        this.color = '#4a4a6a';
        this.shieldHealth = 30;
        break;

      case ZombieType.BERSERKER:
        this.width = 22;
        this.height = 22;
        this.health = 25;
        this.maxHealth = 25;
        this.speed = 45;
        this.damage = 15;
        this.scoreValue = 40;
        this.color = '#6a3a1a';
        break;

      default:
        this.width = 18;
        this.height = 18;
        this.health = 10;
        this.maxHealth = 10;
        this.speed = 30;
        this.damage = 10;
        this.scoreValue = 10;
        this.color = '#4a5a3a';
    }

    this.baseSpeed = this.speed;
  }

  public update(deltaTime: number, playerX: number, playerY: number) {
    // Move towards player
    const dx = playerX - this.x;
    const dy = playerY - this.y;
    const distance = Math.sqrt(dx * dx + dy * dy);

    if (distance > 0) {
      let currentSpeed = this.speed;

      // Berserker gets faster when damaged
      if (this.type === ZombieType.BERSERKER && this.health < this.maxHealth) {
        const damagePercent = 1 - (this.health / this.maxHealth);
        currentSpeed = this.baseSpeed * (1 + damagePercent * 1.5);
        this.enraged = damagePercent > 0.5;
      }

      const moveDistance = (currentSpeed * deltaTime) / 1000;
      this.x += (dx / distance) * moveDistance;
      this.y += (dy / distance) * moveDistance;

      // Poison zombie leaves trail
      if (this.type === ZombieType.POISON) {
        const now = Date.now();
        if (this.poisonTrail.length === 0 || now - this.poisonTrail[this.poisonTrail.length - 1].time > 500) {
          this.poisonTrail.push({ x: this.x, y: this.y, time: now });
          // Keep only recent trail points
          this.poisonTrail = this.poisonTrail.filter(point => now - point.time < 5000);
        }
      }
    }
  }

  public takeDamage(damage: number): boolean {
    // Shield zombie blocks some damage
    if (this.type === ZombieType.SHIELD && this.shieldHealth > 0) {
      const shieldDamage = Math.min(damage, this.shieldHealth);
      this.shieldHealth -= shieldDamage;
      damage -= shieldDamage;

      // If shield is broken, take remaining damage
      if (damage > 0) {
        this.health = Math.max(0, this.health - damage);
      }
      return shieldDamage > 0; // Return true if any damage was blocked
    } else {
      this.health = Math.max(0, this.health - damage);
      return false;
    }
  }

  public getHealthPercentage(): number {
    return this.health / this.maxHealth;
  }

  public getShieldPercentage(): number {
    if (this.type !== ZombieType.SHIELD) return 0;
    return this.shieldHealth / 30; // Max shield health is 30
  }

  public shouldSpawnMinion(): boolean {
    if (this.type !== ZombieType.BOSS) return false;
    const now = Date.now();
    if (now - this.lastMinionSpawn > 3000) { // Spawn every 3 seconds
      this.lastMinionSpawn = now;
      return true;
    }
    return false;
  }

  public getPoisonTrail(): Array<{x: number, y: number, time: number}> {
    return this.poisonTrail;
  }
}

